mapper.select:
SELECT destinationno, recipient, recipient_phone, postcode, address1 ,address2, memno
FROM destination
WHERE destinationno=#{destinationno}

mapper.update:
UPDATE destination
SET recipient=#{recipient}, recipient_phone=#{recipient_phone}, postcode=#{postcode}, address1=#{address1}, address2=#{address2}
WHERE destinationno=#{destinationno}

UPDATE destination
SET basic='A'
WHERE destinationno = #{destinationno} AND memno =#{memno}

UPDATE destination
SET basic='B'
WHERE destinationno != #{destinationno} AND memno =#{memno}
