mapper.select:
SELECT COUNT(mem_id) as cnt
FROM mem
WHERE mem_id=#{mem_id}

SELECT memno, mem_img ,mem_id, mem_pw, mem_name, mem_yy, mem_mm, mem_dd, mem_telecom, mem_phone, mem_email, signdate, memlvno
FROM mem
WHERE mem_id = #{mem_id}

SELECT memno, mem_img ,mem_id, mem_pw, mem_name, mem_yy, mem_mm, mem_dd, mem_telecom, mem_phone, mem_email, signdate, memlvno
FROM mem
WHERE memno = #{memno}

SELECT COUNT(memno) as cnt
FROM mem
WHERE mem_pw=#{mem_pw} AND mem_id=#{mem_id}

SELECT COUNT(memno) as cnt
FROM mem
WHERE memno=#{memno} AND mem_pw=#{mem_pw}

mapper.update:
UPDATE mem
SET mem_pw=#{mem_pw}
WHERE memno=#{memno}

UPDATE mem
SET mem_email=#{mem_email}, mem_telecom=#{mem_telecom}, mem_phone=#{mem_phone}
WHERE memno=#{memno}

UPDATE mem
SET mem_img=#{mem_img}
WHERE memno=#{memno}
